% Regras de recomendação
recomendar_artista(Genero, Sensacao, Ritmo, Artista) :-
    artista(Artista, generos(Generos), sensacao(Sensacoes), ritmo(Ritmos)),
    member(Genero, Generos),
    member(Sensacao, Sensacoes),
    member(Ritmo, Ritmos).

% Função para listar artistas recomendáveis
listar_artistas_recomendaveis(Genero, Sensacao, Ritmo, Lista) :-
    findall(Artista, recomendar_artista(Genero, Sensacao, Ritmo, Artista), Lista).

% Predicado para fazer a consulta
recomendar :-
    write('Digite o genero (ex: pop, rock, jazz, mpb, edm, rnb, indie_rock, samba, pagode, etc): '), read(Genero),
    write('Digite a sensacao (ex: alegre, reflexiva, intensa, relaxante, energizante, poderosa, instrospectiva, emocional, etc): '), read(Sensacao),
    write('Digite o ritmo (ex: rapido, moderado, lento): '), read(Ritmo),
    listar_artistas_recomendaveis(Genero, Sensacao, Ritmo, Lista),
    write('Artistas recomendados: '), write(Lista), nl.

