% Verifica se o artista corresponde ao gênero solicitado
verificar_genero(Artista, Genero) :-
    artista(Artista, generos(Generos), _, _, _, _),
    member(Genero, Generos).

% Verifica se o artista corresponde à sensação solicitada
verificar_sensacao(Artista, Sensacao) :-
    artista(Artista, _, sensacao(Sensacoes), _, _, _),
    member(Sensacao, Sensacoes).

% Verifica se o artista corresponde ao ritmo solicitado
verificar_ritmo(Artista, Ritmo) :-
    artista(Artista, _, _, ritmo(Ritmos), _, _),
    member(Ritmo, Ritmos).

% Verifica se o artista corresponde à década solicitada
verificar_decada(Artista, Decada) :-
    artista(Artista, _, _, _, decada(Decada), _).

% Verifica se o artista corresponde à nacionalidade solicitada
verificar_nacionalidade(Artista, Nacionalidade) :-
    artista(Artista, _, _, _, _, nacionalidade(Nacionalidade)).

% Verifica todos os critérios de recomendação
verificar_todos_criterios(Artista, Genero, Sensacao, Ritmo, Decada, Nacionalidade) :-
    verificar_genero(Artista, Genero),
    verificar_sensacao(Artista, Sensacao),
    verificar_ritmo(Artista, Ritmo),
    verificar_decada(Artista, Decada),
    verificar_nacionalidade(Artista, Nacionalidade).

% Lista os artistas que atendem a todos os critérios
listar_artistas_recomendaveis(Genero, Sensacao, Ritmo, Decada, Nacionalidade, Lista) :-
    findall(Artista, verificar_todos_criterios(Artista, Genero, Sensacao, Ritmo, Decada, Nacionalidade), Lista).

% Busca os artistas semelhantes de um artista
buscar_artistas_semelhantes(Artista, Semelhantes) :-
    artistas_semelhantes(Artista, Semelhantes), !.
buscar_artistas_semelhantes(_, []).


% Predicado para recomendar artistas e seus semelhantes
recomendar :-
    write('Digite o genero (ex: pop, rock, jazz, mpb, edm, rnb, indie_rock, samba, pagode, etc): '), read(Genero),
    write('Digite a sensacao (ex: alegre, reflexiva, intensa, relaxante, energizante, poderosa, introspectiva, emocional, etc): '), read(Sensacao),
    write('Digite o ritmo (ex: rapido, moderado, lento): '), read(Ritmo),
    write('Digite a decada (ex: anos_80, anos_90, anos_2000, anos_2010): '), read(Decada),
    write('Digite a nacionalidade (ex: brasil, eua, inglaterra, jamaica, etc): '), read(Nacionalidade),
    listar_artistas_recomendaveis(Genero, Sensacao, Ritmo, Decada, Nacionalidade, [ArtistaPrincipal|_]),
    buscar_artistas_semelhantes(ArtistaPrincipal, ArtistasSemelhantes),
    write('Artista recomendado: '), write(ArtistaPrincipal), nl,
    write('Artistas semelhantes: '), write(ArtistasSemelhantes), nl.

